SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_dados_conta ( nr_seq_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= '';
cd_conta_contabil_w		varchar(20);
ie_tipo_w				varchar(1);
ie_operacao_w			varchar(1);


BEGIN

/*

CC	- Conta contábil
T	- Tipo
O	- Operação

*/
select	max(cd_conta_contabil),
	max(ie_tipo),
	max(ie_operacao)
into STRICT	cd_conta_contabil_w,
	ie_tipo_w,
	ie_operacao_w
from	gpi_plano
where	nr_sequencia = nr_seq_conta_p;


if (ie_opcao_p = 'CC') then

	ds_retorno_w	:= cd_conta_contabil_w;
elsif (ie_opcao_p = 'T') then

	ds_retorno_w	:= ie_tipo_w;
elsif (ie_opcao_p = 'O') then

	ds_retorno_w	:= ie_operacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
