SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_dados_estagio ( nr_seq_estagio_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*

CF 	Cor da fonte
CG	Cor de fundo do GRID

*/
BEGIN

if (coalesce(nr_seq_estagio_p,0) <> 0) then

	if (ie_opcao_p = 'CF') then

		select	coalesce(max(ds_cor_fonte),'')
		into STRICT	ds_retorno_w
		from	gpi_estagio
		where	nr_sequencia	= nr_seq_estagio_p;

	elsif (ie_opcao_p = 'CG') then

		select	coalesce(max(ds_cor_fundo),'')
		into STRICT	ds_retorno_w
		from	gpi_estagio
		where	nr_sequencia	= nr_seq_estagio_p;

	end if;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
