CREATE OR REPLACE FUNCTION gpi_obter_dados_etapa_proj (nr_sequencia_p bigint, nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);


BEGIN 
	 
	select 	substr(gpi_obter_dados_etapa(max(b.nr_sequencia),'E'),1,255) 
	Into STRICT	ds_retorno_w	 
	from	man_ordem_servico_v a, 
		gpi_cron_etapa b, 
	    gpi_cronograma c	 
	where	a.nr_seq_etapa_gpi		= b.nr_sequencia 
	and	b.nr_seq_cronograma	  	= c.nr_sequencia 
	and	c.nr_seq_projeto		= nr_seq_projeto_p;	
	 
 
	 
	return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
