SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_dados_etapa ( nr_seq_etapa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nm_etapa_w			varchar(100);
nr_seq_estrutura_w			bigint;
nr_seq_cronograma_w		bigint;

BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then
	select	a.nr_seq_superior,
		a.nm_etapa,
		a.nr_seq_cronograma
	into STRICT	nr_seq_estrutura_w,
		nm_etapa_w,
		nr_seq_cronograma_w
	from	gpi_cron_etapa a
	where	a.nr_sequencia = nr_seq_etapa_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= nm_etapa_w;
	elsif (ie_opcao_p = 'E') then
		select	coalesce(max(nm_etapa),'')
		into STRICT	ds_retorno_w
		from	gpi_cron_etapa
		where	nr_sequencia	= nr_seq_estrutura_w;
	elsif (ie_opcao_p = 'C') then
		select	ds_titulo
		into STRICT	ds_retorno_w
		from	gpi_cronograma
		where	nr_sequencia = nr_seq_cronograma_w;
	end if;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
