SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_dados_orcamento (ie_tipo_valor_p text, nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w	gpi_orc_item.vl_orcado%type;				
				
/*
ie_tipo_valor_p

R - Valor real
O - Valor orcamento
*/
				
				

BEGIN

if (ie_tipo_valor_p IS NOT NULL AND ie_tipo_valor_p::text <> '') and (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	
	if (ie_tipo_valor_p = 'O') then
		
		select	sum(CASE WHEN b.ie_operacao='S' THEN  a.vl_orcado WHEN b.ie_operacao='D' THEN (a.vl_orcado * -1) END )
		into STRICT	vl_retorno_w
		from	gpi_plano b,
				gpi_orc_item a,
				gpi_orcamento c
		where	a.nr_seq_plano	= b.nr_sequencia
		and		a.nr_seq_orcamento = c.nr_sequencia
		and		c.nr_seq_projeto = nr_seq_projeto_p
		and     coalesce(a.ie_situacao,'A') = 'A';
		
	else
		
		select	sum(CASE WHEN b.ie_operacao='S' THEN  a.vl_realizado WHEN b.ie_operacao='D' THEN (a.vl_realizado * -1) END )
		into STRICT	vl_retorno_w
		from	gpi_plano b,
				gpi_orc_item a,
				gpi_orcamento c
		where	a.nr_seq_plano	= b.nr_sequencia
		and		a.nr_seq_orcamento = c.nr_sequencia
		and		c.nr_seq_projeto = nr_seq_projeto_p
		and     coalesce(a.ie_situacao,'A') = 'A';
		
	end if;

end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
