CREATE OR REPLACE FUNCTION gpi_obter_dados_projeto ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p:
'N' = Nome projeto
'C' = Código do projeto - CD_PROJETO
'G' = Código do gestor do projeto
*/
nm_projeto_w			varchar(255);
cd_projeto_w			varchar(20);
cd_pf_gestor_w			varchar(10);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	select	nm_projeto,
		cd_projeto,
		cd_pf_gestor
	into STRICT	nm_projeto_w,
		cd_projeto_w,
		cd_pf_gestor_w
	from	gpi_projeto
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (ie_opcao_p = 'C') then
	return	cd_projeto_w;
elsif (ie_opcao_p = 'N') then
	return	nm_projeto_w;
elsif (ie_opcao_p = 'G') then
	return	cd_pf_gestor_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
