CREATE OR REPLACE FUNCTION gpi_obter_descricao_os (nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


nm_projeto_w		varchar(255);
nm_etapa_w		varchar(100);
nm_etapa_superior_w	varchar(100);
nr_seq_projeto_w	bigint;
nr_seq_superior_w	bigint;
ie_descricao_os_w	varchar(2);
ds_retorno_w		varchar(255);
i			integer;


BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then
	select	max(a.nr_seq_projeto)
	into STRICT	nr_seq_projeto_w
	from	gpi_cron_etapa b,
		gpi_cronograma a
	where	a.nr_sequencia	= b.nr_seq_cronograma
	and	b.nr_sequencia	= nr_seq_etapa_p;

	select	nm_etapa
	into STRICT	nm_etapa_w
	from	gpi_cron_etapa
	where	nr_sequencia	= nr_seq_etapa_p;

	select	coalesce(max(ie_descricao_os),'A'),
		max(nm_projeto)
	into STRICT	ie_descricao_os_w,
		nm_projeto_w
	from	gpi_projeto
	where	nr_sequencia	= nr_seq_projeto_w;

	select	max(nr_seq_superior)
	into STRICT	nr_seq_superior_w
	from	gpi_cron_etapa
	where	nr_sequencia	= nr_seq_etapa_p;

	if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
		select	nm_etapa
		into STRICT	nm_etapa_superior_w
		from	gpi_cron_etapa
		where	nr_sequencia = nr_seq_superior_w;
	end if;

	for i in 1..length(ie_descricao_os_w) loop
		begin
		if (substr(ie_descricao_os_w,i,1) = 'A') then
			ds_retorno_w	:= substr(ds_retorno_w || '-' ||nm_etapa_w,1,255);
		elsif (substr(ie_descricao_os_w,i,1) = 'E') then
			ds_retorno_w	:= substr(ds_retorno_w || '-' || nm_etapa_superior_w,1,255);
		elsif (substr(ie_descricao_os_w,i,1) = 'P') then
			ds_retorno_w	:= substr(ds_retorno_w || '-' || nm_projeto_w,1,255);
		end if;
		end;
	end loop;
	ds_retorno_w	:= replace(substr(ds_retorno_w,1,1),'-','') || substr(ds_retorno_w,2,80);
end if;

return	coalesce(ds_retorno_w,nm_etapa_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
