CREATE OR REPLACE FUNCTION gpi_obter_desc_etapa ( nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


nm_etapa_w			varchar(100);


BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then
	select	max(a.nm_etapa)
	into STRICT	nm_etapa_w
	from	gpi_cron_etapa a
	where	a.nr_sequencia = nr_seq_etapa_p;
end if;

return nm_etapa_w;

END;
