SET client_encoding TO 'UTF8';



CREATE TYPE campos AS (	nr_sequencia		bigint,
			ds_estrutura		varchar(100));


CREATE OR REPLACE FUNCTION gpi_obter_estrutura_proj ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


type vetor is table of campos index by integer;
estrutura_w			vetor;
i				integer		:= 0;
nr_sequencia_w			bigint	:= nr_sequencia_p;
nr_seq_superior_w			bigint;
ds_retorno_w			varchar(4000);
ds_estrutura_w			varchar(255);


BEGIN

select	max(nr_seq_estrutura)
into STRICT	nr_sequencia_w
from	gpi_projeto
where	nr_sequencia	= nr_sequencia_p;

/* Obter a sequencia de apresentação da etapa*/

select	max(nr_seq_superior),
	max(ds_estrutura)
into STRICT	nr_seq_superior_w,
	ds_estrutura_w
from	gpi_estrutura
where	nr_sequencia		= nr_sequencia_w;

estrutura_w[i].nr_sequencia	:= nr_sequencia_w;
estrutura_w[i].ds_estrutura	:= ds_estrutura_w;
/* Obter as sequencias de apresentação superiores*/

while(nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') loop
	begin
	nr_sequencia_w		:= nr_seq_superior_w;
	i			:= i + 1;

	select	max(nr_seq_superior),
		max(ds_estrutura)
	into STRICT	nr_seq_superior_w,
		ds_estrutura_w
	from	gpi_estrutura
	where	nr_sequencia	= nr_sequencia_w;

	estrutura_w[i].nr_sequencia	:= nr_sequencia_w;
	estrutura_w[i].ds_estrutura	:= ds_estrutura_w;
	end;
end loop;
/* Define a classificação da etapa */

if (estrutura_w.Count = 1) then
	ds_retorno_w	:= estrutura_w[i].ds_estrutura;
elsif (estrutura_w.Count > 1) then
	begin
	FOR i IN REVERSE estrutura_w.Count-1..0 loop
		begin
		ds_retorno_w	:= substr(ds_retorno_w || estrutura_w[i].ds_estrutura || replace('\ ',' ',''),1,4000);
		end;
	end loop;
	end;
end if;
/* se o ultimo caracter for um ponto, retirar da variavel */

if (substr(ds_retorno_w,length(ds_retorno_w),1) = replace('\ ',' ','')) then
	ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
