CREATE OR REPLACE FUNCTION gpi_obter_nome_etapa ( nr_seq_etapa_gpi_p bigint, nr_seq_proj_gpi_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);

BEGIN
 
	 
 
select		substr(max(b.nm_etapa),1,255) 
Into STRICT		ds_retorno_w 
from		man_ordem_servico_v a, 
		gpi_cron_etapa b, 
	 	 gpi_cronograma c 
where		a.nr_seq_etapa_gpi		= nr_seq_etapa_gpi_p	 
and		a.nr_seq_etapa_gpi		= b.nr_sequencia 
and		b.nr_seq_cronograma	    = c.nr_sequencia 
and		c.nr_seq_projeto		= nr_seq_proj_gpi_p;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
