SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_perc_proj ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


dt_cancelamento_w	timestamp;
qt_cron_w		bigint;
pr_real_w			double precision;
pr_projeto_w		double precision	:= 0;


BEGIN

select	count(*),
	coalesce(sum(pr_real),0)
into STRICT	qt_cron_w,
	pr_real_w
from	gpi_cronograma
where	nr_seq_projeto	= nr_sequencia_p
and	coalesce(dt_cancelamento::text, '') = '';

pr_projeto_w		:= pr_real_w;

if (pr_real_w <> 0) then
	pr_projeto_w	:= (dividir(pr_real_w,(qt_cron_w * 100)) * 100);
end if;

return pr_projeto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
