SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_pf_equipe_etapa (nr_sequencia_p bigint, ie_nome_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(10);
ds_retorno_w			varchar(4000);
ie_pos_virgula_w		bigint;
nm_pessoa_fisica_w		varchar(60);
ds_equipe_w				varchar(80);

c01 CURSOR FOR 
SELECT	b.cd_pessoa_fisica 
from	gpi_cron_etapa_equipe b, 
	gpi_cron_etapa a 
where	a.nr_sequencia	= b.nr_seq_etapa 
and	a.nr_sequencia	= nr_sequencia_p 
and	(b.cd_pessoa_fisica IS NOT NULL AND b.cd_pessoa_fisica::text <> '') 

union all
 
SELECT	d.cd_pessoa_fisica 
from	gpi_proj_Equipe_membro d, 
	gpi_proj_equipe c, 
	gpi_cron_etapa_equipe b, 
	gpi_cron_etapa a 
where	a.nr_sequencia	= b.nr_seq_etapa 
and	b.nr_seq_equipe	= c.nr_sequencia 
and	c.nr_sequencia	= d.nr_seq_equipe 
and	a.nr_sequencia	= nr_sequencia_p 
and	(b.nr_seq_equipe IS NOT NULL AND b.nr_seq_equipe::text <> '');

 
c02 CURSOR FOR 
SELECT	distinct c.ds_equipe 
from	gpi_proj_equipe c, 
	gpi_cron_etapa_equipe b, 
	gpi_cron_etapa a 
where	a.nr_sequencia	= b.nr_seq_etapa 
and	b.nr_seq_equipe	= c.nr_sequencia 
and	a.nr_sequencia	= nr_sequencia_p 
and (not exists (	SELECT 1 
			from gpi_proj_equipe_membro d 
			where 	c.nr_sequencia	= d.nr_seq_equipe));

 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nm_pessoa_fisica_w	:= substr(obter_nome_pf(cd_pessoa_fisica_w),1,60);
	if (ie_nome_p = 'PM') then 
		nm_pessoa_fisica_w	:= substr(obter_primeiro_nome(nm_pessoa_fisica_w),1,60);
	elsif (ie_nome_P = 'AP') then 
		select	coalesce(max(ds_apelido), nm_pessoa_fisica_w) 
		into STRICT	nm_pessoa_fisica_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
	end if;
		 
	ds_retorno_w		:= substr(ds_retorno_w || nm_pessoa_fisica_w || ', ',1,4000);
	end;
end loop;
close C01;
 
 
open C02;
loop 
fetch C02 into 
	ds_equipe_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	ds_retorno_w:= substr(ds_retorno_w || ds_equipe_w || ', ',1,4000);
	end;
end loop;
close c02;
 
ie_pos_virgula_w	:= coalesce(instr(ds_retorno_w, ',',-1),0);
 
if (ie_pos_virgula_w <> 0) then 
	ds_retorno_w	:= substr(ds_retorno_w,1,ie_pos_virgula_w-1);
end if;
 
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
