SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_qt_total_hora_cron (nr_seq_cronograma_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
	'R' = qt_hora_real
	'P' = qt_hora_prev
*/
qt_total_retorno_w	double precision;
qt_total_hora_prev_w	double precision;
qt_total_hora_real_w	double precision;

BEGIN

select 	sum(qt_hora_prev),
	sum(qt_hora_real)
into STRICT	qt_total_hora_prev_w,
	qt_total_hora_real_w
from 	gpi_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p
and	coalesce(nr_seq_superior::text, '') = '';

if (upper(ie_opcao_p) = 'R') then
	qt_total_retorno_w := qt_total_hora_real_w;
elsif (upper(ie_opcao_p) = 'P') then
	qt_total_retorno_w := qt_total_hora_prev_w;
end if;

return	qt_total_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
