SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpi_obter_se_etapa_em_atraso ( nr_seq_superior_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
nr_seq_superior_w	bigint;
qt_registro_w		bigint;
ie_titulo_w		varchar(1);


BEGIN

ie_titulo_w	:= substr(coalesce(gpi_obter_se_etapa_titulo(nr_seq_superior_p),'N'),1,1);
nr_seq_superior_w	:= nr_seq_superior_p;
if (ie_titulo_w = 'S') then
	begin

	select 	count(*)
	into STRICT	qt_registro_w
	from	gpi_cron_etapa
	where	nr_seq_superior = nr_seq_superior_w
	and 	dt_fim_prev < clock_timestamp()
	and	pr_etapa    <> 100;

	if (qt_registro_w > 0) then
		ie_retorno_w	:= 'S';
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
