CREATE OR REPLACE FUNCTION gpi_obter_se_etapa_sup ( nr_seq_etapa_sup_p bigint, nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classif_sup_w			varchar(40);
cd_classif_w			varchar(40);
ie_pertence_w			varchar(1) := 'N';
nr_seq_cronograma_w		bigint;
nr_seq_cronograma_sup_w		bigint;


BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then
	cd_classif_w	:= gpi_obter_classif_etapa(nr_seq_etapa_p);
	cd_classif_sup_w	:= coalesce(gpi_obter_classif_etapa(nr_seq_etapa_sup_p),cd_classif_w);

	select	max(nr_seq_cronograma)
	into STRICT	nr_seq_cronograma_sup_w
	from	gpi_cron_etapa
	where	nr_sequencia	= nr_seq_etapa_sup_p;

	select	max(nr_seq_cronograma)
	into STRICT	nr_seq_cronograma_w
	from	gpi_cron_etapa
	where	nr_sequencia	= nr_seq_etapa_p;

	if (nr_seq_cronograma_sup_w = nr_seq_cronograma_w) and (substr(cd_classif_w,1,length(cd_classif_sup_w)) = cd_classif_sup_w) then
		ie_pertence_w	:= 'S';
	end if;
end if;

return	substr(ie_pertence_w,1,1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
