CREATE OR REPLACE FUNCTION gpi_obter_se_tipo_etapa_proj ( nr_seq_projeto_p bigint, nr_seq_tipo_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w				varchar(1)	:= 'S';
qt_registro_w				bigint;


BEGIN

if (coalesce(nr_seq_projeto_p,0) <> 0) and (coalesce(nr_seq_tipo_etapa_p,0) <> 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	gpi_tipo_etapa_proj
	where	nr_seq_projeto	= nr_seq_projeto_p;

	if (qt_registro_w > 0) then
		select	coalesce(max('S'),'N')
		into STRICT	ie_liberado_w
		from	gpi_tipo_etapa_proj
		where	nr_seq_projeto		= nr_seq_projeto_p
		and	nr_seq_tipo_etapa	= nr_seq_tipo_etapa_p;
	end if;
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
