CREATE OR REPLACE FUNCTION gpi_obter_se_tipo_proj_lib ( nr_seq_tipo_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1);
cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	gpi_tipo_projeto_lib
where	nr_seq_tipo	= nr_seq_tipo_p;

if (cont_w = 0) then
	ie_permite_w	:= 'S';
else
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_permite_w
	from	gpi_tipo_projeto_lib
	where	nr_seq_tipo				= nr_seq_tipo_p
	and	coalesce(cd_perfil,cd_perfil_p)		= cd_perfil_p
	and	coalesce(nm_usuario_lib,nm_usuario_p)	= nm_usuario_p
	and	ie_permite				= 'S'
	and	((coalesce(nr_seq_grupo::text, '') = '') or (obter_se_usuario_grupo(nr_seq_grupo,nm_usuario_p) = 'S'));
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
