SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpt_get_release_status (nm_tabela_p text, nr_sequencia_p bigint, si_param_1_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ie_tipo_item_w	varchar(3);
dt_release_w	timestamp;
si_option_w		varchar(3);
si_return_w		varchar(3);


BEGIN 
 
ie_tipo_item_w	:= cpoe_obter_tipo_item(nm_tabela_p,nr_sequencia_p);
 
if (si_param_1_p = 'E') then 
	si_option_w	:= 'LE';
else 
	si_option_w	:= 'F';
end if;
 
dt_release_w	:= cpoe_obter_dado_prescr(nr_sequencia_p,ie_tipo_item_w,si_option_w);
 
if (dt_release_w IS NOT NULL AND dt_release_w::text <> '') then 
	/* Released */
 
	si_return_w	:= '0';
else 
	/* Pending */
 
	si_return_w	:= '1';
end if;
 
 
return	si_return_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
