SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpt_obter_desc_prot_prescr ( nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

					
ds_retorno_w			varchar(255);

c01 CURSOR FOR
SELECT	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_material a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p in ('M', 'S', 'MAT')

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_procedimento a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p = 'P'

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_gasoterapia a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p = 'GAS'

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_recomendacao a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p = 'R'

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_hemoterapia a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p = 'H'

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo_prescr, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_dialise a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p in ('DP', 'HD')

union all

select	substr(obter_desc_protocolo(a.cd_protocolo),1,100) ds_protocolo,
		substr(obter_desc_protocolo_medic(a.nr_seq_protocolo_prescr, a.cd_protocolo),1,50) ds_subtipo_protocolo
from	cpoe_dieta a
where	a.nr_sequencia	= nr_seq_cpoe_p
and		ie_tipo_item_p in ('D', 'SNE', 'LD', 'S', 'NP', 'NPI', 'J');

BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') then
	if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then
		for	c01_w in c01 loop
		begin
			if (c01_w.ds_protocolo IS NOT NULL AND c01_w.ds_protocolo::text <> '') then
				if (ie_tipo_retorno_p = 'P') then
					ds_retorno_w	:= c01_w.ds_protocolo;
				else
					ds_retorno_w	:= c01_w.ds_subtipo_protocolo;
				end if;					
			end if;
		end;		
		end loop;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
