CREATE OR REPLACE FUNCTION gpt_obter_funcao_origem ( nm_tabela_p text, nr_seq_cpoe_p bigint) RETURNS bigint AS $body$
DECLARE


cd_funcao_origem_w	bigint;


BEGIN

	if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') then

