			into STRICT	cd_funcao_origem_w
			from	cpoe_procedimento 
			where	nr_sequencia = nr_seq_cpoe_p;

		elsif (nm_tabela_p = 'CPOE_GASOTERAPIA') then

			select	coalesce(max(cd_funcao_origem), 2314)
			into STRICT	cd_funcao_origem_w
			from	cpoe_gasoterapia 
			where	nr_sequencia = nr_seq_cpoe_p;

		elsif (nm_tabela_p = 'CPOE_HEMOTERAPIA') then

