CREATE OR REPLACE FUNCTION gpt_obter_info_intervencao (nr_seq_cpoe_p bigint, ie_tipo_item_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_retorno_p	- I (Se contem intervencao no ultimo evento gerado pelo medico)
				- O (Obeservacao)
*/
nr_seq_reval_rule_w		cpoe_revalidation_rule.nr_sequencia%type;
ds_observaca_w	gpt_hist_reval_events_doc.ds_observacao%type;
nr_seq_hist_reval_events_w	gpt_hist_reval_events_doc.nr_sequencia%type;


BEGIN

if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '' AND ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then
	nr_seq_reval_rule_w := cpoe_obter_seq_reval_events(nr_seq_cpoe_p, ie_tipo_item_p);

	if (nr_seq_reval_rule_w IS NOT NULL AND nr_seq_reval_rule_w::text <> '')  then
		select	max(nr_sequencia)
		into STRICT	nr_seq_hist_reval_events_w
		from	gpt_hist_reval_events_doc a
		where	nr_seq_reval_events = nr_seq_reval_rule_w;

		if (ie_retorno_p = 'I') then
			if (nr_seq_hist_reval_events_w IS NOT NULL AND nr_seq_hist_reval_events_w::text <> '') then
				return obter_expressao_sim_nao('S');
			else
				return obter_expressao_sim_nao('N');
			end if;
		elsif (ie_retorno_p = 'O') then
		
			select	max(ds_observacao)
			into STRICT	ds_observaca_w
			from	gpt_hist_reval_events_doc
			where	nr_sequencia = nr_seq_hist_reval_events_w;
		
			return ds_observaca_w;
		end if;
	end if;
end if;

return '';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
