CREATE OR REPLACE FUNCTION gpt_obter_nm_ult_evol_medico ( nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS PESSOA_FISICA.NM_PESSOA_FISICA%TYPE AS $body$
DECLARE


cd_evolucao_w	evolucao_paciente.cd_evolucao%type;
nm_medico_w		pessoa_fisica.nm_pessoa_fisica%type;


BEGIN

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

		select	max(a.cd_evolucao)
		into STRICT	cd_evolucao_w
		from	evolucao_paciente a,
				medico b
		where	a.cd_medico = b.cd_pessoa_fisica
		and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and		a.nr_atendimento = nr_atendimento_p;

		if (cd_evolucao_w IS NOT NULL AND cd_evolucao_w::text <> '') then

			select	substr(obter_nome_medico(cd_medico,'N'),1,255)
			into STRICT	nm_medico_w
			from	evolucao_paciente
			where	cd_evolucao = cd_evolucao_w
			and		nr_atendimento = nr_atendimento_p;

		end if;

	end if;

	return nm_medico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
