CREATE OR REPLACE FUNCTION gpt_obter_prx_adm_doc_rev (nr_atendimento_p bigint) RETURNS PRESCR_MAT_HOR.DT_HORARIO%TYPE AS $body$
DECLARE


dt_primeiro_horario_w	prescr_mat_hor.dt_horario%type;
dt_inicio_prescricao_w	timestamp;


BEGIN

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

		dt_inicio_prescricao_w := gpt_get_doc_reval_period();

		select	min(c.dt_horario)
		into STRICT	dt_primeiro_horario_w
		from	prescr_medica a,
				prescr_material b,
				prescr_mat_hor c,
				cpoe_revalidation_events d,
				cpoe_revalidation_rule e
		where	a.nr_prescricao = b.nr_prescricao
		and		b.nr_seq_mat_cpoe = d.nr_seq_material
		and		b.nr_prescricao = c.nr_prescricao
		and		b.nr_sequencia = c.nr_seq_material
		and		d.nr_seq_revalidation_rule = e.nr_sequencia
		and		(a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '')
		and		coalesce(c.dt_suspensao::text, '') = ''
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		b.ie_agrupador in (1,4)
		and		a.cd_funcao_origem not in (924,950)
		and		coalesce(a.nr_seq_atend::text, '') = ''
		and		a.dt_prescricao > dt_inicio_prescricao_w
		and		c.dt_horario > d.dt_validacao
		and		d.dt_validacao < clock_timestamp()
		and		d.dt_validacao between dt_inicio_prescricao_w and fim_dia(clock_timestamp())
		and		a.nr_atendimento = nr_atendimento_p;

	end if;

	return	dt_primeiro_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
