CREATE OR REPLACE FUNCTION gpt_obter_regra_reval (cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE

			
c01 CURSOR FOR

	SELECT		nr_sequencia
	from		gpt_revalidation_rule
	where		((coalesce(cd_setor_atendimento::text, '') = '') or (cd_setor_atendimento_p = cd_setor_atendimento))
	and     	((coalesce(cd_estabelecimento::text, '') = '') or (cd_estabelecimento_p = cd_estabelecimento))
	and 		coalesce(ie_situacao, 'A') = 'A'
	order by	coalesce(cd_setor_atendimento, 99999) desc,
				coalesce(cd_estabelecimento, 9999) desc;


nr_seq_revalidation_rule_w 	bigint;
				

BEGIN

open c01;
loop
fetch c01 into

		nr_seq_revalidation_rule_w;
		
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		null;
	end;
end loop;
close c01;

	return 		nr_seq_revalidation_rule_w;	

end;
$body$
