CREATE OR REPLACE FUNCTION gpt_obter_se_conf_sem_ciencia ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w varchar(1);

BEGIN

ie_retorno_w  := 'N';

select coalesce(max('S'), 'N')
into STRICT   ie_retorno_w
from   gpt_nao_conformidades_v
where  coalesce(ie_ciente,'N') = 'N'
and    nr_prescricao      = nr_prescricao_p;

return ie_retorno_w;
end;
$body$
