CREATE OR REPLACE FUNCTION gpt_obter_se_existe_mot_baixa (nr_seq_cpoe_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_w		varchar(1)	:= 'N';


BEGIN

if (nr_seq_cpoe_material_p IS NOT NULL AND nr_seq_cpoe_material_p::text <> '') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_existe_w
	from	prescr_material a
	where	a.nr_seq_mat_cpoe = nr_seq_cpoe_material_p
	and		coalesce(a.cd_motivo_baixa,0) <> 0;
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
