CREATE OR REPLACE FUNCTION gpt_obter_se_horario_validade (nr_seq_item_p bigint, ie_tipo_item_p text, ie_validar_dt_fim_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_horarios_w		varchar(255);
ds_hora_w		varchar(255);
ds_horarios_retorno_w	varchar(255) := '';
dt_inicio_w	timestamp;
dt_fim_w	timestamp;
dt_validade_w	timestamp;
dt_horario_w	timestamp;
qt_dia_adic_w	bigint;
k	bigint;


BEGIN

select 	padroniza_horario_prescr(ds_horarios, CASE WHEN coalesce(0,0)=0 THEN CASE WHEN substr(Obter_Se_horario_hoje(dt_inicio,dt_prim_horario,hr_prim_horario),1,1)='N' THEN '01/01/2000 23:59:59'  ELSE to_char(coalesce(dt_prim_horario,dt_inicio),'dd/mm/yyyy hh24:mi:ss') END   ELSE '01/01/2000 23:59:59' END ),
 	dt_inicio,
 	dt_fim,
	((dt_inicio + 1) - 1/86400)
into STRICT 	ds_horarios_w,
 	dt_inicio_w,
 	dt_fim_w,
	dt_validade_w
from 	cpoe_material
where 	nr_sequencia	= nr_seq_item_p;

qt_dia_adic_w:= 0;

if (ie_validar_dt_fim_p = 'S') then
	if (dt_fim_w < dt_validade_w) then
		return 'S';
	end if;
end if;

if (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
	while(ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') LOOP
		select	position(' ' in ds_horarios_w)
		into STRICT	k
		;

		if (k > 1) and ((substr(ds_horarios_w, 1, k -1) IS NOT NULL AND (substr(ds_horarios_w, 1, k -1))::text <> '')) then
			ds_hora_w		:= substr(ds_horarios_w, 1, k-1);
			ds_hora_w		:= replace(ds_hora_w, ' ','');
			ds_horarios_w	:= substr(ds_horarios_w, k + 1, 2000);
		elsif (ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') then
			ds_hora_w 		:= replace(ds_horarios_w,' ','');
			ds_horarios_w	:= '';
		end if;

		if (position('A' in ds_hora_w) > 0) then
			qt_dia_adic_w	:= 1;
		end if;
		if (position('AA' in ds_hora_w) > 0) then
			qt_dia_adic_w	:= qt_dia_adic_w + 1;
		end if;

		ds_hora_w	:= replace(ds_hora_w,'A','');
		ds_hora_w	:= replace(ds_hora_w,'A','');

		dt_horario_w	:= to_date(to_char(dt_inicio_w + coalesce(qt_dia_adic_w,0),'dd/mm/yyyy')||' '||replace(ds_hora_w,'A','')||':00','dd/mm/yyyy hh24:mi:ss');

		if (dt_horario_w < dt_inicio_w) then
			dt_horario_w	:= dt_horario_w + 1;
		end if;

		if (dt_horario_w		> dt_fim_w) then
			ds_horarios_retorno_w	:= ds_horarios_retorno_w || ' ' ||ds_hora_w;
		end if;

	end loop;
end if;

return	trim(both ds_horarios_retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
