CREATE OR REPLACE FUNCTION gpt_obter_se_item_liberado ( nr_sequencia_p prescr_material.nr_seq_mat_cpoe%type, ie_tipo_liberacao_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(1);


BEGIN

if (ie_tipo_liberacao_p = 'E') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_liberado_w
	from	cpoe_material
	where	nr_sequencia = nr_sequencia_p
	and		(nm_usuario_lib_enf IS NOT NULL AND nm_usuario_lib_enf::text <> '')
	and 	(dt_liberacao_enf IS NOT NULL AND dt_liberacao_enf::text <> '');

else

	select	coalesce(max('S'),'N')
	into STRICT	ie_liberado_w
	from	cpoe_material
	where	nr_sequencia = nr_sequencia_p
	and		(nm_usuario_lib_farm IS NOT NULL AND nm_usuario_lib_farm::text <> '')
	and 	(dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '');

end if;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
