CREATE OR REPLACE FUNCTION gpt_obter_se_item_lib_ant (nr_seq_mat_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'N';
ie_liberado_w	varchar(1)	:= 'N';
ie_enfermeiro_w varchar(1);
nr_prescricao_w	prescr_material.nr_prescricao%type;
nr_sequencia_w	prescr_material.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	nr_prescricao,
			nr_sequencia
	from	prescr_material a
	where	a.nr_seq_mat_cpoe = nr_seq_mat_cpoe_p;


BEGIN

ie_enfermeiro_w := Obter_Param_Usuario(252, 1, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_enfermeiro_w);

open C01;
loop
fetch C01 into
	nr_prescricao_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_liberado_w := gpt_obter_se_item_liberado(nr_seq_mat_cpoe_p, ie_enfermeiro_w);

	if (ie_liberado_w = 'S') then
		ie_retorno_w := 'S';
		exit;
	end if;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
