SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gpt_obter_se_pend_reval (nr_seq_cpoe_p bigint, ie_tipo_item_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_validacao_w				timestamp;
ie_pendente_w				varchar(1) := 'N';


BEGIN

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') then
		
		select	max(dt_validacao)
		into STRICT	dt_validacao_w
		from	gpt_revalidation_events
		where	ie_tipo_item = ie_tipo_item_p
		and		nr_atendimento = nr_atendimento_p
		and		nr_seq_cpoe = nr_seq_cpoe_p;
		
		if (dt_validacao_w < clock_timestamp()) then
			
			if (ie_tipo_item_p in ('M', 'MAT', 'SOL')) then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from	cpoe_material
				where	nr_sequencia   = nr_seq_cpoe_p
				and		nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and  	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih,dt_fim)  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp())
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  coalesce(dt_fim_cih,dt_fim)  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));
				
				
			elsif (ie_tipo_item_p in ('D', 'J', 'SNE', 'LD', 'S', 'NPTA', 'NPTI')) then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from	cpoe_dieta
				where	nr_sequencia   = nr_seq_cpoe_p
				and		nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp())
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));
				
			
			elsif (ie_tipo_item_p = 'P') then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from 	cpoe_procedimento
				where	nr_sequencia   = nr_seq_cpoe_p
				and     nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp())
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));

			
			elsif (ie_tipo_item_p = 'R') then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from 	cpoe_recomendacao 
				where	nr_sequencia   = nr_seq_cpoe_p 
				and     nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp())  
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));

				
			elsif (ie_tipo_item_p = 'O') then
		
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from 	cpoe_gasoterapia
				where	nr_sequencia   = nr_seq_cpoe_p
				and     nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp()) 
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));

				
			elsif (ie_tipo_item_p = 'HM') then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from 	cpoe_hemoterapia
				where	nr_sequencia   = nr_seq_cpoe_p
				and     nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp()) 
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));
				

				
			elsif (ie_tipo_item_p in ('D','DI','DP')) then
			
				select	coalesce(max('S'), 'N')
				into STRICT	ie_pendente_w
				from 	cpoe_dialise
				where	nr_sequencia   = nr_seq_cpoe_p
				and     nr_atendimento = nr_atendimento_p
				and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and     (dt_liberacao_farm IS NOT NULL AND dt_liberacao_farm::text <> '')
				and 	((CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) END  >= clock_timestamp())  
				or (CASE WHEN coalesce(dt_lib_suspensao::text, '') = '' THEN  dt_fim  ELSE coalesce(dt_suspensao,dt_fim) coalesce(END::text, '') = ''));

			
			end if;
			
		end if;
	
	end if;
	
	return ie_pendente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
