CREATE OR REPLACE FUNCTION gpt_obter_status_intervencao ( qt_itens_pend_p integer, qt_itens_reval_p integer ) RETURNS varchar AS $body$
DECLARE


ie_status_intervencao_w varchar(2);


BEGIN

	ie_status_intervencao_w := 'PE'; -- Pendente
	if (qt_itens_pend_p = qt_itens_reval_p AND qt_itens_pend_p <> 0) then
		ie_status_intervencao_w := 'CO'; -- Completa
	elsif (qt_itens_pend_p > qt_itens_reval_p AND qt_itens_reval_p <> 0) then
		ie_status_intervencao_w := 'PA'; -- Parcial
	elsif (qt_itens_pend_p > 0 AND qt_itens_reval_p = 0) then
		ie_status_intervencao_w := 'PE'; -- Pendente
	end if;

	return ie_status_intervencao_w;

end;
