CREATE OR REPLACE FUNCTION gpt_obter_ult_doc_reval (nr_atendimento_p bigint) RETURNS CPOE_REVALIDATION_EVENTS.DT_VALIDACAO%TYPE AS $body$
DECLARE


dt_atualizacao_nrec_w   cpoe_revalidation_events.dt_atualizacao_nrec%type := null;
dt_val_inicio_w		    cpoe_revalidation_events.dt_validacao%type := null;


BEGIN
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

		dt_val_inicio_w := inicio_dia(clock_timestamp() - interval '3 days');

			select		max(a.dt_atualizacao_nrec)
			into STRICT		dt_atualizacao_nrec_w
			from		cpoe_revalidation_events a,
						cpoe_revalidation_rule b
			where		a.nr_seq_revalidation_rule = b.nr_sequencia
			and			a.nr_atendimento = nr_atendimento_p
			and			(a.dt_validacao - (b.qt_horas_anterior / 24) - 1) < clock_timestamp()
			and			a.dt_validacao between dt_val_inicio_w and fim_dia(clock_timestamp());

	end if;

	return dt_atualizacao_nrec_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
