CREATE OR REPLACE FUNCTION gpw_obter_se_tabela_pend_os ( nr_sequencia_p bigint, nr_seq_modulo_impl_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
nr_seq_ordem_serv_w		bigint;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	select	coalesce(max(nr_seq_ordem_serv),0)
	into STRICT	nr_seq_ordem_serv_w
	from	gpw_pendencia
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_ordem_serv_w > 0) then
		begin

		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	tabela_sistema
		where	ie_situacao <> 'I'
		and	ie_cadastro_geral = 'S'
		and	nr_seq_ordem_serv = nr_seq_ordem_serv_w  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;

		if (ds_retorno_w = 'S') then
			begin
			select	'M'
			into STRICT	ds_retorno_w
			from	tabela_sistema
			where	ie_situacao <> 'I'
			and	ie_cadastro_geral = 'S'
			and	nr_seq_mod_impl = coalesce(nr_seq_modulo_impl_p,nr_seq_mod_impl)
			and	(nr_seq_mod_impl IS NOT NULL AND nr_seq_mod_impl::text <> '')
			and	nr_seq_ordem_serv = nr_seq_ordem_serv_w
			and not exists (	SELECT	1
					from	tabela_sistema
					where	ie_situacao <> 'I'
					and	ie_cadastro_geral = 'S'
					and	coalesce(nr_seq_mod_impl::text, '') = ''
					and	nr_seq_ordem_serv = nr_seq_ordem_serv_w)  LIMIT 1;
			exception
			when others then
				ds_retorno_w := 'S';
			end;
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
