CREATE OR REPLACE FUNCTION gp_obter_local_pront_barras ( nr_atendimento_p bigint default 0, nm_usuario_p text DEFAULT NULL, cd_estabelecimento_p bigint DEFAULT NULL, nr_prontuario_p bigint default 0, ie_forma_gera_pront_p text DEFAULT NULL, nr_conta_p bigint default 0, nr_prescricao_p bigint default 0) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);						
ds_local_w		varchar(50);
ds_local_concatenado_w	varchar(4000);
ds_retorno_w		varchar(4000);

C01 CURSOR FOR 
	SELECT	distinct substr(Obter_Desc_Local_Same_Regra(a.nr_seq_local,nm_usuario_p,cd_estabelecimento_p),1,50) 
	from	same_prontuario a, 
		same_caixa b 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	a.nr_seq_caixa = b.nr_sequencia;
			

BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	if (ie_forma_gera_pront_p = 'NUNCA') or (ie_forma_gera_pront_p = 'BASE') then	 
		 
		select max(cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	pessoa_fisica 
		where	nr_prontuario = nr_prontuario_p;	
	 
	elsif (ie_forma_gera_pront_p = 'ESTAB') then 
		 
		select	max(a.cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	pessoa_fisica a, 
			pessoa_fisica_pront_estab b 
		where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
		and	b.nr_prontuario = nr_prontuario_p 
		and	b.cd_estabelecimento = cd_estabelecimento_p;			
		 
	end if;			
end if;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	select	max(a.cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente a, 
		conta_paciente b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	b.nr_interno_conta = nr_conta_p;
end if;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	prescr_medica 
	where	nr_prescricao = nr_prescricao_p;
end if;
 
open C01;
loop 
fetch C01 into	 
	ds_local_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_local_concatenado_w := ds_local_concatenado_w||chr(10)||ds_local_w;
	end;
end loop;
close C01;
 
if (ds_local_concatenado_w IS NOT NULL AND ds_local_concatenado_w::text <> '') then 
	ds_retorno_w := substr(ds_local_concatenado_w,1,4000);	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
