SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gqa_obter_acao_desc_grid (nr_sequencia_p bigint, ie_mostrar_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w          varchar(4000) := '';

  acao RECORD;

BEGIN
  if (coalesce(nr_sequencia_p::text, '') = '') then
    return null;
  end if;

  for acao in (select * from gqa_acao where nr_sequencia = nr_sequencia_p) loop
    select
           ds_retorno_w || (case when acao.ie_tipo_acao = 'AV' then 
                    obter_desc_tipo_avaliacao(acao.nr_seq_avaliacao)
                when acao.ie_tipo_acao = 'ES' then 
                    obter_desc_score_flex_2(acao.nr_seq_score_flex_ii)
                when acao.ie_tipo_acao = 'DL' then 
                    acao.cd_doenca
                when acao.ie_tipo_acao = 'EP' then (select ds_regra 
                    from gqa_pendencia_regra 
                    where nr_sequencia = acao.nr_seq_acao_protocolo)
                when acao.ie_tipo_acao = 'PP' then 
                    obter_desc_protocolo(acao.cd_protocolo) || 
                      (select ' | ' || nm_medicacao
                      from protocolo_medicacao
                      where cd_protocolo = acao.cd_protocolo
                      and nr_seq_interna = acao.nr_seq_protocolo)
                when acao.ie_tipo_acao = 'PG' then (select ds_pendencia 
                    from gqa_pendencia 
                    where nr_sequencia = acao.nr_seq_protocolo_ger)
                else acao.ds_acao end) ds
    into STRICT ds_retorno_w
;

    if ((ie_mostrar_tipo_p IS NOT NULL AND ie_mostrar_tipo_p::text <> '') and ie_mostrar_tipo_p = 'S') then
      select obter_valor_dominio(9520, acao.ie_tipo_acao) || ' | ' || ds_retorno_w into STRICT ds_retorno_w;
    end if;
  end loop;

  if (length(ds_retorno_w) = 0) then
    return null;
  end if;

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
