CREATE OR REPLACE FUNCTION gqa_obter_acao_prot_ger (nr_sequencia_p bigint, tipo_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_protocolo_ger_w             bigint := null;


BEGIN

  if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND tipo_p IS NOT NULL AND tipo_p::text <> '') then
    if (tipo_p = 'M') then
      begin
            select mentor.nr_seq_protocolo_ger into STRICT nr_seq_protocolo_ger_w from GQA_PEND_PAC_ACAO mentor where mentor.nr_sequencia = nr_sequencia_p;
      exception when no_data_found then
            nr_seq_protocolo_ger_w := null;
      end;
    elsif (tipo_p = 'C') then
      begin
            select cadastro.nr_seq_protocolo_ger into STRICT nr_seq_protocolo_ger_w from GQA_ACAO cadastro where cadastro.nr_sequencia = nr_sequencia_p;
      exception when no_data_found then
            nr_seq_protocolo_ger_w := null;
      end;
    end if;
  end if;

  return nr_seq_protocolo_ger_w;

end;
