CREATE OR REPLACE FUNCTION gqa_obter_acao_titulo (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_acao_w        gqa_acao.ie_tipo_acao%type;
ds_acao_w             gqa_acao.ds_acao%type;
ds_retorno_w          varchar(1000) := null;


BEGIN

  if (coalesce(nr_sequencia_p::text, '') = '') then
    return ds_retorno_w;
  end if;

  begin
    select t.ie_tipo_acao, t.ds_acao into STRICT ie_tipo_acao_w, ds_acao_w from gqa_acao t where t.nr_sequencia = nr_sequencia_p  LIMIT 1;
  exception when no_data_found then
    return ds_retorno_w;
  end;

  if (coalesce(ie_tipo_acao_w::text, '') = '') then
    return ds_retorno_w;
  end if;

  if (ie_tipo_acao_w = 'PG') then
	  select obter_desc_expressao(296595) into STRICT ds_retorno_w;
  elsif (ie_tipo_acao_w = 'DL') then
	  select obter_desc_expressao(324228) into STRICT ds_retorno_w;
  elsif (ie_tipo_acao_w = 'MC') then
	  select obter_desc_expressao(601319) into STRICT ds_retorno_w;
  else
    select ds_acao_w into STRICT ds_retorno_w;
  end if;

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
