CREATE OR REPLACE FUNCTION gqa_obter_protocolo_progresso (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


date_protocolo_w      gqa_protocolo_pac.dt_prevista_fim%type;
date_etapa_w          gqa_protocolo_etapa_pac.dt_prevista_fim%type;
minutes_diff_w        bigint;
minutes_total_w       bigint;
ds_retorno_w          double precision;
iniciado_w            smallint;


BEGIN

  select CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN  0  ELSE 1 END , dt_prevista_fim into STRICT iniciado_w, date_protocolo_w from gqa_protocolo_pac where nr_sequencia = nr_sequencia_p;

  if (iniciado_w = 0) then
    return 0;
  end if;

  -- ultima etapa finalizada
  begin
        select dt_prevista_fim into STRICT date_etapa_w from (
                  SELECT dt_prevista_fim from gqa_protocolo_etapa_pac where nr_seq_prot_pac = nr_sequencia_p and (dt_fim IS NOT NULL AND dt_fim::text <> '') order by dt_fim desc
        ) alias1 LIMIT 1;
  exception when no_data_found then
        date_etapa_w := null;
  end;

  if (coalesce(date_etapa_w::text, '') = '') then
    return 0;
  end if;

  -- diferenca entre protocolo.dt_fim_previsto e etapa.dt_fim_previsto
  select trunc(abs(date_etapa_w - date_protocolo_w)*24*60)
        into STRICT minutes_diff_w;

  -- quantidade total de minutos previstos para o protocolo
  select trunc(abs(max(a1.dt_prevista_fim) - min(a1.dt_prevista_inicio))* 24 * 60)
        into STRICT minutes_total_w from gqa_protocolo_etapa_pac a1 where a1.nr_seq_prot_pac = nr_sequencia_p;

  -- calculando complemento do minutes_diff_w
  select abs(minutes_diff_w - minutes_total_w) into STRICT minutes_diff_w;

  -- calculando porcentagem de diferenca
    begin
      select (minutes_diff_w / minutes_total_w) into STRICT ds_retorno_w;
    exception when others then
      ds_retorno_w := 0;
    end;

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
