CREATE OR REPLACE FUNCTION gqa_obter_quantidade_prot (cd_pessoa_fisica_p text, nr_atendimento_p bigint, tipo_p text) RETURNS bigint AS $body$
DECLARE


qtd_w             bigint := 0;


BEGIN

  if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
    select count(1) into STRICT qtd_w
    from gqa_protocolo_pac where nr_atendimento = nr_atendimento_p;
  elsif ((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and tipo_p = 'T') then
    -- se parametro for A, totalizar protocolos do paciente
    -- do contrario, nao deve mostrar o total do paciente
    select count(1) into STRICT qtd_w
    from gqa_protocolo_pac where cd_pessoa_fisica = cd_pessoa_fisica_p;
  end if;

  return qtd_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
