CREATE OR REPLACE FUNCTION gratuidade_gerar_campo_proc ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50) := '';
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_proc_interno_w	bigint;
ie_classificacao_w		varchar(1);


BEGIN

select	cd_procedimento,
	ie_origem_proced,
	coalesce(nr_seq_proc_interno,0)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w
from	procedimento_paciente
where	nr_sequencia = nr_sequencia_p;

select	ie_classificacao
into STRICT	ie_classificacao_w
from	procedimento
where	cd_procedimento = cd_procedimento_w
and	ie_origem_proced = ie_origem_proced_w;

if (ie_classificacao_w = 3) then
	begin
	if (nr_seq_proc_interno_w in (4663,4664,5001,5043,5008,5047,5045,5046,5339)) then
		ds_retorno_w := upper(substr(wheb_mensagem_pck.get_texto(300041),1,50)); --APARTAMENTO
	elsif (nr_seq_proc_interno_w in (4666,5002,5044,5009,5048)) then
		ds_retorno_w := upper(substr(wheb_mensagem_pck.get_texto(300043),1,50)); --ENFERMARIA
	elsif (nr_seq_proc_interno_w in (4667,4900,4665)) then
		ds_retorno_w := upper(substr(wheb_mensagem_pck.get_texto(300044),1,50)); --UTI
	end if;
	end;
else
	ds_retorno_w	:= nr_seq_proc_interno_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
