CREATE OR REPLACE FUNCTION grg_obter_datas_item (nr_seq_propaci_p bigint, nr_seq_matpaci_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;

/*ie_opcao_p
E	Data de execução
C	Data conta
*/
BEGIN

if (ie_opcao_p = 'E') then

	if (coalesce(nr_seq_propaci_p,0) > 0) then

		select	max(a.dt_procedimento)
		into STRICT	dt_retorno_w
		from	procedimento_paciente a
		where	a.nr_sequencia	= nr_seq_propaci_p;

	elsif (coalesce(nr_seq_matpaci_p,0) > 0) then

		select	max(coalesce(a.dt_prescricao, a.dt_conta))
		into STRICT	dt_retorno_w
		from	material_atend_paciente a
		where	a.nr_sequencia	= nr_seq_matpaci_p;

	end if;
elsif (ie_opcao_p = 'C') then

	if (coalesce(nr_seq_propaci_p,0) > 0) then

		select	max(a.dt_conta)
		into STRICT	dt_retorno_w
		from	procedimento_paciente a
		where	a.nr_sequencia	= nr_seq_propaci_p;

	elsif (coalesce(nr_seq_matpaci_p,0) > 0) then

		select	max(a.dt_conta)
		into STRICT	dt_retorno_w
		from	material_atend_paciente a
		where	a.nr_sequencia	= nr_seq_matpaci_p;

	end if;

end if;

return 	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
