CREATE OR REPLACE FUNCTION grg_obter_se_itens_pago (nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


count_itens_w	bigint;
count_pago_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

select	count(*)
into STRICT	count_itens_w
from	lote_audit_hist_item
where	nr_seq_guia		= nr_seq_guia_p;

select	count(*)
into STRICT	count_pago_w
from	lote_audit_hist_item
where	nr_seq_guia		= nr_seq_guia_p
and	ie_pago_convenio	= 'S';

if (count_itens_w <> count_pago_w) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
