CREATE OR REPLACE FUNCTION gv_obter_se_status_gera_atend ( ie_status_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
if (ie_status_p IS NOT NULL AND ie_status_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	gv_regra_atend_status;

	if (ds_retorno_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	gv_regra_atend_status
		where	ie_status = ie_status_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
