CREATE OR REPLACE FUNCTION habilita_reverte_transf_mapa (nr_atendimento_p bigint, nr_seq_leito_p bigint) RETURNS varchar AS $body$
DECLARE


ie_habilita varchar(1) := 'N';

nr_seq_interno_w atend_paciente_unidade.nr_seq_interno%type;
cd_tipo_acomodacao_w atend_paciente_unidade.cd_tipo_acomodacao%type;


BEGIN

nr_seq_interno_w:= obter_atepacu_paciente(nr_atendimento_p, 'UAA'); -- UAA = Unidade Anterior a atual
if ((nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') and nr_seq_interno_w <> 0) then
  select cd_tipo_acomodacao
  into STRICT cd_tipo_acomodacao_w
  from	atend_paciente_unidade
  where	nr_seq_interno = nr_seq_interno_w;

  if (obter_se_sem_acomodacao(cd_tipo_acomodacao_w) = 'S'
    or obter_se_transf_solict(nr_seq_leito_p) = 'S')then
    ie_habilita:= 'S';
  end if;
end if;


return	ie_habilita;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
