SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hagf_obter_qtd_contas (nr_interno_conta_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE

				 
qt_contas_w		bigint := 0;
				

BEGIN 
if (ie_opcao_p = 1) then 
	select 	count(distinct b.nr_interno_conta) 
	into STRICT	qt_contas_w 
	from	atendimento_paciente a, 
		conta_paciente b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	b.nr_interno_conta = nr_interno_conta_p 
	and	a.ie_tipo_atendimento = 1;
end if;
 
if (ie_opcao_p <> 1) then 
	select 	count(distinct b.nr_interno_conta) 
	into STRICT	qt_contas_w 
	from	atendimento_paciente a, 
		conta_paciente b 
	where	a.nr_atendimento = b.nr_atendimento 
	and	b.nr_interno_conta = nr_interno_conta_p 
	and	a.ie_tipo_atendimento <> 1;
end if;
 
return	coalesce(qt_contas_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
