CREATE OR REPLACE FUNCTION has_filter_configuration ( nr_seq_filtro_p bigint, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


qt_records_w	bigint;
ds_return_w	varchar(1) := 'N'; --No
BEGIN

select  count(*)
into STRICT    qt_records_w
FROM dic_objeto_filtro c, wfiltro_atrib_regra b
LEFT OUTER JOIN wfiltro_atrib_regra_item a ON (b.nr_sequencia = a.nr_seq_atrib_regra)
WHERE b.nr_seq_wfiltro = c.nr_sequencia and (c.nm_atributo = nm_atributo_p or coalesce(nm_atributo_p::text, '') = '') and c.nr_seq_objeto =  nr_seq_filtro_p;

if (qt_records_w > 0) then
	ds_return_w := 'Y'; --Yes
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
