SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION has_popup_configuration ( nr_seq_popup_p bigint, nr_seq_sub_popup_p bigint) RETURNS varchar AS $body$
DECLARE


qt_records_w	bigint;
ds_return_w	varchar(1) := 'N'; --No
BEGIN

select  count(*)
into STRICT    qt_records_w
from    funcao_popup_regra a,
	dic_objeto b
where   a.nr_seq_objeto = b.nr_sequencia
and (
		b.nr_sequencia = nr_seq_sub_popup_p or
		coalesce(nr_seq_sub_popup_p::text, '') = ''
	)
and	b.nr_seq_obj_sup = nr_seq_popup_p;

if (qt_records_w > 0) then
	ds_return_w := 'Y'; --Yes
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
