CREATE OR REPLACE FUNCTION has_procedure_item_no_pbs ( NR_PRESCRICAO_P PRESCR_MATERIAL.NR_PRESCRICAO%TYPE, NR_CIRURGIA_P MATERIAL_ATEND_PACIENTE.NR_CIRURGIA%TYPE ) RETURNS varchar AS $body$
DECLARE

    COUNT_ITEMS     bigint;
    IE_RETORNO_W    CONVENIO_PLANO.IE_EPS%TYPE;

BEGIN
  SELECT  coalesce(MAX(IS_HEALTH_INSURANCE_PLAN_EPS(A.NR_ATENDIMENTO)), 'N')
  INTO STRICT    IE_RETORNO_W
  FROM    PRESCR_MEDICA A
  WHERE   A.NR_PRESCRICAO = NR_PRESCRICAO_P;

  IF (IE_RETORNO_W = 'S') THEN
    SELECT  coalesce(SUM(HAS), 0)
    INTO STRICT    COUNT_ITEMS
    FROM (
      SELECT  1 HAS
      FROM    PRESCR_PROCEDIMENTO A,
              CIRURGIA B
      WHERE   B.NR_PRESCRICAO = A.NR_PRESCRICAO
      AND     A.NR_PRESCRICAO = NR_PRESCRICAO_P
      AND     IS_PROCEDURE_NO_PBS(A.CD_PROCEDIMENTO, A.IE_ORIGEM_PROCED) = 'S'
      AND     HAS_REGISTER_MIPRES(A.NR_PRESCRICAO, B.NR_CIRURGIA, A.NR_SEQUENCIA, 'P') = 'N'

UNION ALL

      SELECT  1 HAS
      FROM    CIRURGIA A
      WHERE   NR_CIRURGIA = NR_CIRURGIA_P
      AND     IS_PROCEDURE_NO_PBS(A.CD_PROCEDIMENTO_PRINC, A.IE_ORIGEM_PROCED) = 'S'
      AND     HAS_REGISTER_MIPRES(A.NR_PRESCRICAO, A.NR_CIRURGIA, 1, 'P') = 'N'
      
UNION ALL

      SELECT  1 HAS
      FROM    PROCEDIMENTO_PACIENTE A
      WHERE   A.NR_CIRURGIA = NR_CIRURGIA_P
      AND     coalesce(A.CD_MOTIVO_EXC_CONTA::text, '') = ''
      AND     A.QT_PROCEDIMENTO > 0
      AND     IS_PROCEDURE_NO_PBS(A.CD_PROCEDIMENTO, A.IE_ORIGEM_PROCED) = 'S'
      AND     HAS_REGISTER_MIPRES(A.NR_PRESCRICAO, A.NR_CIRURGIA, A.NR_SEQUENCIA_PRESCRICAO, 'P') = 'N'
    ) alias10;

    IF (COUNT_ITEMS = 0) THEN
      IE_RETORNO_W := 'N';
    END IF;
  END IF;
  RETURN IE_RETORNO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
