SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION has_wcpanel_configuration ( nr_seq_wcpanel_p bigint, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


qt_records_w	bigint;
ds_return_w	varchar(1) := 'N'; --No
BEGIN

select  count(*)
into STRICT    qt_records_w
from    wcp_regra_atributo a,
        dic_objeto b,
        dic_objeto c
where   a.nr_seq_wcpanel = b.nr_sequenciA
and     a.nr_seq_atributo = c.nr_sequencia
and     c.nr_seq_obj_sup = b.nr_sequencia
and     c.ie_tipo_objeto = 'AC'
and     coalesce(c.ie_visible, 'S') = 'S'
and (c.nm_campo_base = nm_atributo_p or coalesce(nm_atributo_p::text, '') = '')
and     b.nr_sequencia =  nr_seq_wcpanel_p;

if (qt_records_w > 0) then
	ds_return_w := 'Y'; --Yes
end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
